console.log("\u{1F680} Crypto Sumo Extension - Background script loaded");chrome.runtime.onInstalled.addListener(e=>{console.log("Extension installed:",e.reason),e.reason==="install"&&chrome.storage.sync.set({cryptoData:{},settings:{refreshInterval:3e4,notifications:!0}})});chrome.runtime.onMessage.addListener((e,s,t)=>{switch(console.log("Message received:",e),e.action){case"getCryptoData":t({success:!0,data:"crypto data here"});break;case"updateSettings":chrome.storage.sync.set({settings:e.settings},()=>{t({success:!0})});break;default:t({success:!1,error:"Unknown action"})}return!0});
